import React from 'react';
import PropTypes from 'prop-types';
import TareaContext from '../../context/tareas/tareaContext';


const Tarea = ({ tarea }) => {

    const {agregarTarea,validarTarea,errortarea,obtenerTareas}=useContext(tareaContext);// Se obtienen datos del context de tareas


    return (
        <li className="tarea sombra">
            <p>{tarea.nombre}</p>
            <div className="estado">
                {tarea.estado ? (
                    <button
                        type="button"
                        className="completo"
                    >Completo</button>
                )
                    : (<button
                        type="button"
                        className="incompleto"
                    >Incompleto</button>)
                }
            </div>

            <div className="acciones">
                <button type="button" className="btn btn-primario">Editar</button>
                <button type="button" className="btn btn-secundario">Eliminar</button>
            </div>
        </li>
    );
};

Tarea.propTypes = {

};

export default Tarea;
