import PropTypes from 'prop-types';

const Tarea = ({tarea}) => {
    return (
        <li className="tarea sombra">
            <p>{tarea.nombre}</p>
            <div className="estado">
            {tarea.estado ? (
                <button
                type="button"
                className="completo"
                >Completo</button>
            )
            : ( <button
                type="button"
                className="incompleto"
                >Incompleto</button>)
            }
            </div>

            <div className="acciones">
<button></button>
            </div>
        </li>
    );
};

