import React, { useReducer } from 'react';
import proyectoContext from './proyectoContext';
import proyectoReducer from './proyectoReducer';
import uuid from 'uuid/v4';
import {
    FORMULARIO_PROYECTO,
    OBTENER_PROYECTOS,
    AGREGAR_PROYECTO,
    VALIDAR_FORMULARIO
} from '../../types';

// El archivo dentro de types al llamarse index.js no necesita llamarse types/index.js, así como está funciona

const ProyectoState = props => {

    const proyectos=[
        { id:1, nombre: 'Tienda Virtual' },
        {id:2, nombre: 'Intranet' },
        {id:3, nombre: 'Diseño de sitio' },
        {id:4, nombre: 'MERN' }
    ]
    const initialState={
        formulario:false,
        proyectos:[],
        errorformulario:false,
        proyecto:null
    }

    const [state,dispatch]=useReducer(proyectoReducer,initialState);

    const mostrarFormulario= () =>{
        dispatch({
            type:FORMULARIO_PROYECTO
        })
    }

    const obtenerProyectos=()=>{
        dispatch({
            type:OBTENER_PROYECTOS,
            payload:proyectos
        })
    }

    const agregarProyecto=proyecto=>{
        proyecto.id=uuid();

        dispatch({
            type:AGREGAR_PROYECTO,
            payload:proyecto
        })
    }

    const mostrarError=()=>{
        dispatch({
            type:VALIDAR_FORMULARIO
        })
    }

    // Es recomendable manejar los datos del state y luego las funciones en el value del context
    return (
        <proyectoContext.Provider
        value={{
            proyectos:state.proyectos,
            formulario:state.formulario,
            errorformulario:state.errorformulario,
            mostrarFormulario,
            obtenerProyectos,
            agregarProyecto,
            mostrarError

        }}
        >
            {props.children}
        </proyectoContext.Provider>
    );
};


export default ProyectoState;
