import React,{useContext} from 'react';
import PropTypes from 'prop-types';
import TareaContext from '../../context/tareas/tareaContext';
import proyectosContext from '../../context/proyectos/proyectoContext';

const Tarea = ({ tarea }) => {

    const { proyecto } = proyectosContext;
    const {eliminarTarea,obtenerTareas}=useContext(TareaContext);// Se obtienen datos del context de tareas

    const [proyectoActual]=proyectoActual;

    const tareaEliminar=id=>{
        eliminarTarea(id);
        obtenerTareas(proyectoActual.id);
    }

    return (
        <li className="tarea sombra">
            <p>{tarea.nombre}</p>
            <div className="estado">
                {tarea.estado ? (
                    <button
                        type="button"
                        className="completo"
                    >Completo</button>
                )
                    : (<button
                        type="button"
                        className="incompleto"
                    >Incompleto</button>)
                }
            </div>

            <div className="acciones">
                <button type="button" className="btn btn-primario">Editar</button>
                <button type="button" className="btn btn-secundario" onClick={()=>tareaEliminar(tarea.id)}>Eliminar</button>
            </div>
        </li>
    );
};

Tarea.propTypes = {

};

export default Tarea;
