import React,{useContext} from 'react';
import PropTypes from 'prop-types';
import TareaContext from '../../context/tareas/tareaContext';
import proyectosContext from '../../context/proyectos/proyectoContext';

const Tarea = ({ tarea }) => {

    const { proyecto } = useContext(proyectosContext);
    const {eliminarTarea,obtenerTareas, cambiaEstadoTarea,guardarTareaActual}=useContext(TareaContext);// Se obtienen datos del context de tareas

    const [proyectoActual]=proyecto;

    const tareaEliminar=id=>{
        eliminarTarea(id);
        obtenerTareas(proyectoActual.id);
    }

    const cambiarEstado=tarea=>{

        if(tarea.estado){
            tarea.estado=false
        }
        else{
            tarea.estado=true
        }

        cambiaEstadoTarea(tarea);
    }

    const seleccionarTarea=tarea=>{
        guardarTareaActual(tarea)
    }

    return (
        <li className="tarea sombra">
            <p>{tarea.nombre}</p>
            <div className="estado">
                {tarea.estado ? (
                    <button
                        type="button"
                        className="completo"
                        onClick={()=>{cambiarEstado(tarea)}}
                    >Completo</button>
                )
                    : (<button
                        type="button"
                        className="incompleto"
                        onClick={()=>{cambiarEstado(tarea)}}
                    >Incompleto</button>)
                }
            </div>

            <div className="acciones">
                <button type="button" className="btn btn-primario" onClick={()=>seleccionarTarea(tarea)}>Editar</button>
                <button type="button" className="btn btn-secundario" onClick={()=>tareaEliminar(tarea.id)}>Eliminar</button>
            </div>
        </li>
    );
};

Tarea.propTypes = {

};

export default Tarea;
