import React,{useContext,useState} from 'react';
import PropTypes from 'prop-types';
import proyectoContext from '../../context/proyectos/proyectoContext';
import tareaContext from '../../context/tareas/tareaContext';

const FormTarea = props => {

    const { proyecto } = useContext(proyectoContext);//Se obtienen datos del context de proyectos
    const {agregarTarea}=useContext(tareaContext);// Se obtienen datos del context de tareas

    const [tarea,guardarTarea]=useState({
        nombre:''
    })

    if(!proyecto) return null

    // Array destructuring que extrae el proyecto actual
    const [proyectoActual]= proyecto;

    const handleChange=e=>{
        guardarTarea({
            ...tarea,
            [e.target.name]: e.target.value
        })
    }
    const {nombre}= tarea;

    const onSubmit=e=>{
        e.preventDefault();

        tarea.proyectoId=proyectoActual.id;
        tarea.estado=false
        agregarTarea()

    }


    return (
        <div className="formulario">
            <form onSubmit={
                onSubmit
            }>
                <div className="contener-input">
                    <input type="text" className="input-text" placeholder="Nombre Tarea.." name="nombre" onChange={handleChange} value={nombre}/>
                </div>
                <div className="contener-input">
                    <input type="submit" className="btn btn-primario btn-block" value="Agregar Tarea"/>
                </div>
            </form>
        </div>
    );
};

FormTarea.propTypes = {

};

export default FormTarea;
